import React, { useState, useEffect } from 'react'
import { HiMenuAlt4, HiX } from 'react-icons/hi'
import { motion } from 'framer-motion'

import './Navbar.scss'
import { navVariants } from '../../utils/motion'

const Navbar = () => {
   const [toggle, setToggle] = useState(false)
   const [active_blur, setActive_blur] = useState(false)

   const handleScroll = () => {
      const offset = window.scrollY
      if (offset > 30) {
         setActive_blur(true)
      } else {
         setActive_blur(false)
      }
   }

   useEffect(() => {
      window.addEventListener('scroll', handleScroll)
   })

   return (
      <motion.nav
         variants={ navVariants }
         initial='hidden'
         whileInView='show'
         className={ `app__navbar ${ active_blur ? 'active_blur' : '' }` }
      >
         <div className='app__navbar-main'>
            <a href='#home' className='app__navbar-logo'>
               my portfolio
            </a>
            <div className="app__footer-cards">
               <div className="app__footer-card ">
                  <img src="/img/email.png" alt="email" />
                  <a href="mailto:vladmir_37@mail.ru" className="p-text">
                     vladmir_37@mail.ru
                  </a>
               </div>
               <div className="app__footer-card">
                  <img src="/img/phone.png" alt="phone" />
                  <p href="#" className="p-text">
                     +7 (910) 977-77-77
                  </p>
               </div>
            </div>

            <div className='app__navbar-menu'>
               <HiMenuAlt4 onClick={ () => setToggle(true) } />
               { toggle && (
                  <motion.div
                     whileInView={ { x: [200, 0] } }
                     transition={ { duration: 0.65, ease: 'easeIn' } }
                  >
                     <HiX onClick={ () => setToggle(false) } />
                     <ul>
                        { ['home', 'works', 'technologies', 'contacts'].map(
                           (item, index) => (
                              <li key={ index } className='app__flex p-text'>
                                 <a
                                    href={ `#${ item }` }
                                    onClick={ () => setToggle(false) }
                                 >
                                    { item }
                                 </a>
                              </li>
                           )
                        ) }
                     </ul>
                  </motion.div>
               ) }
            </div>
         </div>
      </motion.nav>
   )
}

export default Navbar
