import React, { useState, useEffect } from 'react'
import { motion } from 'framer-motion'

import { AppWrap, MotionWrap } from '../../wrapper'
import { urlFor, client } from '../../client'
import './Skills.scss'



const Skills = () => {
  const [abouts, setAbouts] = useState([])

  useEffect(() => {
     setAbouts([])
    const query = '*[_type == "abouts"] | order(description asc)'

    client.fetch(query).then((data) => setAbouts(data))
  }, [])

  return (
     <>
        <div className="app__about">
           <h2 className="head-text">
               Skills
           </h2>

           <div className="app__profiles">
              {abouts.map((about, index) => (
                 <motion.div
                    whileInView={{ opacity: 1 }}
                    whileHover={{ scale: 1.1 }}
                    transition={{ duration: 0.5, type: 'tween∂' }}
                    className="app__profile-item"
                    key={about.title + index}
                 >
                    { about.imgUrl && <img src={urlFor(about.imgUrl)} alt="about.title" />}
                    <h4 className="skills__text" style={{ marginTop: 20 }}>
                       {about.title}
                    </h4>

                 </motion.div>
              ))}
           </div>
        </div>
     </>
  )
}

export default AppWrap(MotionWrap(Skills, 'app__about'), 'skills')
