import React, { useState } from 'react'
import { motion } from 'framer-motion'
import { AppWrap, MotionWrap } from '../../wrapper'
import { slideIn, staggerContainer, textVariant } from '../../utils/motion'
import { client } from '../../client'
import './Contacts.scss'

const Contacts = () => {
   const [formData, setFormData] = useState({
      name: '',
      email: '',
      message: '',
   })
   const [isFormSubmitted, setIsFormSubmitted] = useState(false)
   const [loading, setLoading] = useState(false)
   const [messageEmpty, setMessageEmpty] = useState('')
   const [animateCard, setAnimateCard] = useState({ y: 0, opacity: 1 })
   const { username, email, message } = formData

   const handleChangeInput = (e) => {
      const { name, value } = e.target
      setFormData({ ...formData, [name]: value })
   }

   const handleSubmit = () => {
      setLoading(true)

      if (!formData.username || !formData.email || !formData.message) {
         setLoading(false)
         setMessageEmpty('Please fill in all fields')
         setTimeout(() => {
            setMessageEmpty('')
         }, 5000)
         return
      }

      const contact = {
         _type: 'contact',
         name: formData.username,
         email: formData.email,
         message: formData.message,
      }

      client
         .create(contact)
         .then(() => {
            setLoading(false)
            setIsFormSubmitted(true)
         })
         .catch((err) => console.log(err))

      setMessageEmpty('')
   }

   return (
      <motion.div
         variants={ staggerContainer }
         initial='hidden'
         whileInView='show'
         viewport={ { once: true, amount: 0.25 } }
         className='app__header'>
         <h2 className='contacts-text'>Contacts</h2>
         <div className='app__footer-cards'>
            <div className='app__footer-card '>
               <img src='/img/email.png' alt='email' />
               <a href='mailto:vladmir_37@mail.ru' className='p-text'>
                  vladmir_37@mail.ru
               </a>
            </div>
            <div className='app__footer-card'>
               <img src='/img/phone.png' alt='phone' />
               <p href='#' className='p-text'>
                  +7 (910) 970-83-32
               </p>
            </div>
         </div>
         { messageEmpty && (
            <motion.div
               variant={ textVariant(1.1) }
               transition={ { duration: 1, delayChildren: 1 } }
            >
               <p className='message'>{ messageEmpty }</p>
            </motion.div>
         ) }
         { !isFormSubmitted ? (
            <div className='app__footer-form app__flex'>
               <div className='app__flex'>
                  <input
                     className='p-text'
                     type='text'
                     placeholder='Your Name'
                     name='username'
                     value={ username }
                     onChange={ handleChangeInput }
                  />
               </div>
               <div className='app__flex'>
                  <input
                     className='p-text'
                     type='email'
                     placeholder='Your Email'
                     name='email'
                     value={ email }
                     onChange={ handleChangeInput }
                  />
               </div>
               <div>
                  <textarea
                     className='p-text'
                     placeholder='Your Message'
                     value={ message }
                     name='message'
                     onChange={ handleChangeInput }
                  />
               </div>
               <button type='button' className='p-text' onClick={ handleSubmit }>
                  { !loading ? 'Send Message' : 'Sending...' }
               </button>
            </div>
         ) : (
            <div>
               <h3 className='head-text'>Thank you for getting in touch!</h3>
            </div>
         ) }
      </motion.div>
   )
}

export default AppWrap(MotionWrap(Contacts, 'app__footer'), 'contacts')
