import React from 'react'


const Pagination = ({ postsPerPage, totalPosts, paginate, paginateBack, paginateFront, currentPage }) => {

   const pageNumbers = []

   for (let i = 1; i <= Math.ceil(totalPosts / postsPerPage); i++) {
      pageNumbers.push(i)
   }

   return (
      <div>
         <nav className='block flex justify-center items-center'>
            <ul className='flex gap-1 pl-0 rounded list-none flex-wrap'>
               <li
                  onClick={ paginateBack }
                  className={ `bg-white border-0 ${ currentPage > 1 ? 'text-gray-500' : 'text-gray-200' } hover:bg-blue-200 relative inline-flex items-center px-4 py-2 border text-sm font-medium` }>
                  { '<' }
               </li>
               { pageNumbers.map((number, idx) => (
                  <li key={ idx }
                      onClick={ () => {
                         paginate(number)
                      } }
                      className={
                         currentPage === number
                            ? 'bg-blue-200 border-red-300 rounded-full text-red-500 hover:bg-blue-200 relative inline-flex items-center px-4 py-2 border text-sm font-medium'
                            : 'bg-white border-gray-300 rounded-full text-gray-500 hover:bg-blue-200 relative inline-flex items-center px-4 py-2 border text-sm font-medium'
                      }
                  >
                     { number }
                  </li>
               )) }
               <li onClick={ paginateFront }
                   className={ `bg-white border-0 ${ currentPage < pageNumbers.length ? 'text-gray-500' : 'text-gray-200' } hover:bg-blue-200 relative inline-flex items-center px-4 py-2 border text-sm font-medium` }>
                  { '>' }
               </li>
            </ul>
         </nav>
      </div>
   )
}

export default Pagination
