import React from 'react'

import './Pagination.scss'

const Pagination = ({
   postsPerPage,
   totalWorks,
   paginate,
   paginateBack,
   paginateFront,
   currentPage,
}) => {
   const pageNumbers = []

   for (let i = 1; i <= Math.ceil(totalWorks / postsPerPage); i++) {
      pageNumbers.push(i)
   }

   return (
      <>
         <nav className="pagin__nav">
            <ul className="pagin__ul">
               <li onClick={paginateBack} className="pagin__li_next">
                  {'<'}
               </li>
               {pageNumbers.map((number, idx) => (
                  <li
                     key={idx}
                     onClick={() => {
                        paginate(number)
                     }}
                     className={
                        currentPage === number
                           ? 'pagin__li active'
                           : 'pagin__li'
                     }
                  >
                     {number}
                  </li>
               ))}
               <li onClick={paginateFront} className="pagin__li_next">
                  {'>'}
               </li>
            </ul>
         </nav>
      </>
   )
}

export default Pagination
