import React, { useState, useEffect } from 'react'
import { HiMenuAlt4, HiX } from 'react-icons/hi'
import { motion } from 'framer-motion'

import './Navbar.scss'
import { navVariants } from '../../utils/motion'

const Navbar = () => {
   const [toggle, setToggle] = useState(false)
   const [active_blur, setActive_blur] = useState(false)

   const handleScroll = () => {
      const offset = window.scrollY
      if (offset > 30) {
         setActive_blur(true)
      } else {
         setActive_blur(false)
      }
   }

   useEffect(() => {
      window.addEventListener('scroll', handleScroll)
   })

   return (
      <motion.nav
         variants={ navVariants }
         initial='hidden'
         whileInView='show'
         className={ `app__navbar ${ active_blur ? 'active_blur' : '' }` }
      >
         <div className='app__navbar-main'>
            <a href='#home' className='app__navbar-logo'>
               My Portfolio
            </a>
            <ul className='app__navbar-links'>
               <li className='p-text'>
                  <a href=''>email: </a>
               </li>
               <li className='p-text'>
                  <a href=''>tel: </a>
               </li>
            </ul>

         </div>
      </motion.nav>
   )
}

export default Navbar
