import React, { useState, useEffect } from 'react'
import { AiFillEye, AiFillGithub } from 'react-icons/ai'
import { motion } from 'framer-motion'

import { AppWrap, MotionWrap } from '../../wrapper'
import { urlFor, client } from '../../client'
import './Work.scss'
import Pagination from '../../components/Pagination/Pagination'

const Work = () => {
   const [activeFilter, setActiveFilter] = useState('All')
   const [animateCard, setAnimateCard] = useState({ y: 0, opacity: 1 })
   const [works, setWorks] = useState([])
   const [filterWork, setFilterWork] = useState([])

   const postsPerPage = 8
   const [currentPage, setCurrentPage] = useState(1)
   const pageNumbers = Math.ceil(filterWork.length / postsPerPage)
   const indexOfLastPost = currentPage * postsPerPage
   const indexOfFirstPost = indexOfLastPost - postsPerPage
   const paginateFront = () =>
      currentPage < pageNumbers
         ? setCurrentPage(currentPage + 1)
         : setCurrentPage(currentPage)
   const paginateBack = () =>
      currentPage > 1
         ? setCurrentPage(currentPage - 1)
         : setCurrentPage(currentPage)
   const paginate = (pageNumber) => setCurrentPage(pageNumber)

   const totalWorks = filterWork.length
   const currentWorks = filterWork.slice(indexOfFirstPost, indexOfLastPost)

   useEffect(() => {
      const query = '*[_type == "works"]'
      client.fetch(query).then((data) => {
         setWorks(data)
         setFilterWork(data)

      })
   }, [])

   const handleWorkFilter = (item) => {
      setActiveFilter(item)
      setAnimateCard([{ y: 100, opacity: 0 }])
      setCurrentPage(1)

      setTimeout(() => {
         setAnimateCard([{ y: 0, opacity: 1 }])

         if (item === 'All') {
            setFilterWork(works)
         } else {
            setFilterWork(works.filter((work) => work.tags.includes(item)))
         }
      }, 500)
   }

   return (
      <div className="app__works">
         <h2 className="works__text">
            Projects
         </h2>
         <div className="app__work-filter">
            {[
               'All',
               'HTML CSS JS',
               'React',
               'Vue',
               'Django',
               'Laravel',
            ].map((item, index) => (
               <div
                  key={index}
                  onClick={() => handleWorkFilter(item)}
                  className={`app__work-filter-item ${
                     activeFilter === item ? 'item-active' : ''
                  }`}
               >
                  <p>{item}</p>
               </div>
            ))}
         </div>
         <motion.div
            animate={animateCard}
            transition={{ duration: 1, delayChildren: 1 }}
            className="app__work-portfolio"
         >
            {currentWorks?.map((work, index) => (
               <div
                  className="app__work-item" key={index}>
                  <div className="app__work-img">
                     <img src={urlFor(work.imgUrl)} alt={work.name} />
                  </div>

                  <div className="app__work-content ">
                     <h4>{work.title}</h4>
                     <p>{work.description}</p>
                     <p className="p__author">
                        <span>Author:</span> SVeM
                     </p>
                     <p className="p__date">
                        <span>Date:</span> 01.01.2023
                     </p>
                     <div className="app__work-tag ">
                        <p>{work.tags[1]} </p>
                     </div>
                  </div>
                  <motion.div
                     whileHover={{ opacity: [0, 1] }}
                     transition={{
                        duration: 0.25,
                        ease: 'easeInOut',
                        staggerChildren: 0.5,
                     }}
                     className="app__work-hover"
                  >
                     <a
                        href={work.projectLink}
                        target="_blank"
                        rel="noreferrer"
                     >
                        <motion.div
                           whileInView={{ scale: [0, 1] }}
                           whileHover={{ scale: [1, 0.9] }}
                           transition={{ duration: 0.25 }}
                        >
                           <AiFillEye />
                        </motion.div>
                     </a>
                     <a href={work.codeLink} target="_blank" rel="noreferrer">
                        <motion.div
                           whileInView={{ scale: [0, 1] }}
                           whileHover={{ scale: [1, 0.9] }}
                           transition={{ duration: 0.25 }}
                        >
                           <AiFillGithub />
                        </motion.div>
                     </a>
                  </motion.div>
               </div>
            ))}
            {!currentWorks.length && (
               <div className='in__process'>
                  <h1>In the process of being placed in the portfolio</h1>
               </div>
            )}
            <Pagination
               postsPerPage={postsPerPage}
               totalWorks={totalWorks}
               paginate={paginate}
               paginateBack={paginateBack}
               paginateFront={paginateFront}
               currentPage={currentPage}
            />
         </motion.div>
      </div>
   )
}

export default AppWrap(
   MotionWrap(Work, 'app__works'),
   'projects',
   'app__works_bg_'
)
